package com.xpwi.action;import com.xpwi.service.impl.SearchForAllServiceImpl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;import java.util.HashMap;import java.util.List;import java.util.Map;@Controller@Scope("prototype")public class SearchForAllAction {    @Autowired    SearchForAllServiceImpl searchForAllService;    @RequestMapping(value = "/search.do",method = RequestMethod.GET)    @ResponseBody    public Map<String, Object> searchForAll(String searchInput) {        List<Map<String, String>> maps = searchForAllService.searchForAll(searchInput);        System.out.println("【搜素关键词】"+searchInput+maps);        Map map1 = new HashMap();        map1.put("data", maps);        if (maps != null)            map1.put("msg", "查询成功");        else            map1.put("msg", "还没有上传文件");        map1.put("code", 0);        return map1;    }}