package com.xpwi.dao.impl;import com.xpwi.dao.IDownloadFileDAO;import org.apache.ibatis.session.SqlSession;import org.apache.ibatis.session.SqlSessionFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;@Repositorypublic class DownloadFileDAOImpl implements IDownloadFileDAO {    @Autowired    SqlSessionFactory sqlSessionFactoryBean;    @Override    public int updateDownloadCount(int file_id) {        SqlSession sqlSession = sqlSessionFactoryBean.openSession(true);        String sql = "com.xpwi.mapping.downloadFile.updateDownloadCount";        int update = sqlSession.update(sql, file_id);        sqlSession.commit();        return update;    }}