package com.xpwi.dao.impl;import com.xpwi.dao.IEChartsDAO;import org.apache.ibatis.session.SqlSession;import org.apache.ibatis.session.SqlSessionFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import java.util.List;import java.util.Map;@Repositorypublic class EChartsDAOImpl implements IEChartsDAO {    @Autowired    SqlSessionFactory sqlSessionFactoryBean;    @Override    public List<Map> dirAnalysis(String user_id) {        SqlSession sqlSession = sqlSessionFactoryBean.openSession(true);        String sql="com.xpwi.mapping.ECharts.dirAnalysis";        List<Map> objects = sqlSession.selectList(sql, user_id);        return objects;    }}