package com.xpwi.dao.impl;import com.xpwi.dao.ISearchForAllDAO;import org.apache.ibatis.session.SqlSession;import org.apache.ibatis.session.SqlSessionFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import java.util.HashMap;import java.util.List;import java.util.Map;@Repositorypublic class SearchForAllDAOImpl implements ISearchForAllDAO {    @Autowired    SqlSessionFactory sqlSessionFactoryBean;    @Override    public List<Map<String, String>> searchForAll(String str) {        System.out.println(str);        Map map = new HashMap();        map.put("name_str",str);        try {            String sql = "com.xpwi.mapping.search.searchForAll";            SqlSession sqlSession = sqlSessionFactoryBean.openSession(true);            List<Map<String,String>> objects = sqlSession.selectList(sql, map);            System.out.println("【模糊查询】"+objects);            return objects;        }catch (Exception e){            e.printStackTrace();        }        return null;    }}