package com.xpwi.dao.impl;import com.xpwi.dao.IUploadFileDAO;import org.apache.ibatis.session.SqlSession;import org.apache.ibatis.session.SqlSessionFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import java.util.HashMap;import java.util.Map;@Repositorypublic class UploadFileDAOImpl implements IUploadFileDAO {    @Autowired    SqlSessionFactory sqlSessionFactoryBean;    @Override    public int uploadPhoto(String userid,String photoPath) {        System.out.println(userid);        String sql = "com.xpwi.mapping.upload.uploadHead";        SqlSession sqlSession = sqlSessionFactoryBean.openSession(true);        Map map = new HashMap<>();        map.put("userId",userid);        map.put("photoPath",photoPath);        System.out.println("123456"+map);        int update = sqlSession.update(sql, map);        sqlSession.commit();        System.out.println(update);        return update;    }    @Override    public Map<String, Object> findFilePathById(String dir_id) {        String sql = "com.xpwi.mapping.upload.findFilePathById";        SqlSession sqlSession = sqlSessionFactoryBean.openSession(true);        Map<String,Object> map = sqlSession.selectOne(sql, dir_id);        return map;    }    @Override    public int insertUploadFileInfo(Map map) {        SqlSession sqlSession = sqlSessionFactoryBean.openSession(true);        String sql = "com.xpwi.mapping.upload.insertUploadFileInfo";        int i = sqlSession.insert(sql,map);        sqlSession.commit();        return i;    }}