package com.xpwi.pojo;public class UserInfo {    private int user_id;    private String username;    private String runame;    private String gender;    private String address;    private String birthday;    private String email;    private String telephone;    private String remark;    public int getUser_id() {        return user_id;    }    public void setUser_id(int user_id) {        this.user_id = user_id;    }    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }    public String getRuname() {        return runame;    }    public void setRuname(String runame) {        this.runame = runame;    }    public String getGender() {        return gender;    }    public void setGender(String gender) {        this.gender = gender;    }    public String getAddress() {        return address;    }    public void setAddress(String address) {        this.address = address;    }    public String getBirthday() {        return birthday;    }    public void setBirthday(String birthday) {        this.birthday = birthday;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public String getTelephone() {        return telephone;    }    public void setTelephone(String telephone) {        this.telephone = telephone;    }    public String getRemark() {        return remark;    }    public void setRemark(String remark) {        this.remark = remark;    }}