package com.xpwi.service.impl;import com.xpwi.dao.IDownloadFileDAO;import com.xpwi.dao.impl.DownloadFileDAOImpl;import com.xpwi.service.IDownloadFileService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class DownloadFileServiceImpl implements IDownloadFileService {    @Autowired    IDownloadFileDAO downloadFileDAOImpl;    @Override    public int updateDownloadCount(int file_id) {        int i = downloadFileDAOImpl.updateDownloadCount(file_id);        return i;    }}