package com.xpwi.service.impl;import com.xpwi.dao.IUploadFileDAO;import com.xpwi.service.IUploadFileService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Map;@Servicepublic class UploadFileServiceImpl implements IUploadFileService {    @Autowired    IUploadFileDAO uploadFileDAOImpl;    @Override    public int uploadPhoto(String userid, String photoPath) {        int i = uploadFileDAOImpl.uploadPhoto(userid, photoPath);        return i;    }    @Override    public Map<String, Object> findFilePathById(String dir_id) {        Map<String, Object> pathById = uploadFileDAOImpl.findFilePathById(dir_id);        return pathById;    }    @Override    public int insertUploadFileInfo(Map map) {        int i = uploadFileDAOImpl.insertUploadFileInfo(map);        return i;    }}